import { useDispatch } from "react-redux";
import { cartActions } from "../../store/cart-slice";
import { Link } from "react-router-dom";

import classes from "./ProductItem.module.css";
import Card from "../UI/Card";
import Button from "../UI/Button";
import { useState } from "react";

const ProductItem = (props) => {
  const { category, price, description, src, id } = props;
  const [isMouseOver, setIsMouseOver] = useState(false);
  const [currency, setCurrency] = useState({
    newPrice: price,
    symbol: "€"
  });

  const dispatch = useDispatch();

  const addToCartHandler = () => {
    dispatch(
      cartActions.addItemToCart({
        id,
        category,
        price,
        src,
        description,
      })
    );
  };

  const changePriceToLei = () => {
    setCurrency({
      newPrice: (currency.newPrice * 5).toFixed(2),
      symbol: "lei"
    });
  }

  const changePriceToEuro = () => {
    setCurrency({
      newPrice: (currency.newPrice / 5).toFixed(2),
      symbol: "€"
    });
  }

  return (
    <li>
      <Card>
        <div className={classes.card}>
          <img src={src} alt="jewel" className={classes.jewelImg} />
          <div className={classes.price} 
            onMouseEnter={() => {setIsMouseOver(true)}}
            onMouseLeave={() => {setIsMouseOver(false)}}>
            {isMouseOver && <p>{`${price} €`}</p>}
            {isMouseOver && <p>{`${price/5} lei`}</p>}
          </div>
          <div className={classes.actions}>
            <Button onClick={addToCartHandler} className={classes.btn}>Add to Cart</Button>
            <Link className={classes.btn} to={`/jewelry/${id}`}>
              <Button>View Details</Button>
            </Link>
          </div>
        </div>
      </Card>
    </li>
  );
};

export default ProductItem;
