          <DetailsItem />
        </Route>
        <Route path="/favorites">
          <Favorites />
        </Route>
        <Route path="/login">
          <Login />
        </Route>
        <Route path="*">
          <NotFound />
        </Route> */}
      </Switch>
      {/* <Footer /> */}
    </Layout>
  );
}

export default App;
