import styles from "./DetailsItem.module.css";
import Card from "../UI/Card";
import { useParams } from "react-router-dom";
import dummy_products from "../../assets/dummy_products";

const DetailsItem = () => {
    const params = useParams();

    // do this using firebase backend
    const loadedData = dummy_products;

    const jewel = loadedData.find(item => item.id === params.jewelId);

  return (
    <Card className={styles.container}>
      <h1>Page with details</h1>
      <div>
        <p>{jewel.category}</p>
        <img src={jewel.src} alt="jewel" />
        <p>{jewel.description}</p>
        <p>{jewel.price}</p>
      </div>
    </Card>
  );
};

export default DetailsItem;
