import { Button } from "bootstrap";
import { Card } from "react-bootstrap";
import styles from "../components/UI/Login.module.css";

const Login = (props) => {

  const [user, setUser] = useState({
    enteredUsername: "",
    enteredAPassword: "",
  });

  const submitHandler = (event) => {
    event.preventDefault(); 

    console.log(user);

    // we reset the inputs of the form after submitting
    setUser({
      enteredUsername: "",
      enteredPassword: "",
    });
  };

  const usernameChangeHandler = (event) => {
    let newUsername = event.target.value;

    setUser((prevInfo) => {
      return {
        ...prevInfo,
        enteredUsername: newUsername,
      };
    });
  };

  const passwordChangeHandler = (event) => {
    let newPassword = event.target.value;

    setUser((prevInfo) => {
      return {
        ...prevInfo,
        enteredPassword: newPassword,
      };
    });
  };

  return (
    <Card>
      <form onSubmit={submitHandler}>
        <label htmlFor="username" placeholder="Username" />
        <input
          id="username"
          type="text"
          onChange={usernameChangeHandler}
          value={user.enteredUsername}
        />

        <label htmlFor="password" placeholder="Password"/>
        <input
          id="age"
          type="password"
          onChange={passwordChangeHandler}
          value={user.enteredPassword}
        />
        <Button type="submit">Login</Button>
      </form>
    </Card>
  );
};

export default Login;
