import { useSelector } from "react-redux";
import { useState } from "react";

import Card from "../UI/Card";
import classes from "./Cart.module.css";
import CartItem from "./CartItem";
import Checkout from "./Checkout";

const Cart = (props) => {
  const cartItems = useSelector((state) => state.cart.items);

  const cartQuantity = useSelector((state) => state.cart.totalQuantity);
  const cartTotal = Math.abs(
    useSelector((state) => state.cart.totalAmount).toFixed(2)
  );

  const [isCheckout, setIsCheckout] = useState(false);
  const orderHandler = () => {
    setIsCheckout(true);
  }

  const sumbitOrderHandler = (userData) => {
    fetch("https://react-app-9c2a2-default-rtdb.europe-west1.firebasedatabase.app/");
  }

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        {cartItems.map((item) => (
          <CartItem
            key={item.id}
            item={{
              id: item.id,
              title: item.name,
              quantity: item.quantity,
              total: item.totalPrice,
              price: item.price,
              src: item.src
            }}
          />
        ))}
      </ul>

      <span className={classes.badge}>{cartQuantity}</span>
      <span className={classes.badge}>${cartTotal}</span>

      {isCheckout && <Checkout onConfirm={sumbitOrderHandler} />}

      {!isCheckout &&<button onClick={orderHandler}>Order</button>}

    </Card>
  );
};

export default Cart;
