import ImageSlider from "../components/UI/ImageSlider";
import styles from "../components/UI/Welcome.module.css";

const Welcome = () => {
  const hour = new Date().getHours();
  let message = "";

  if (hour >= 0 && hour < 12) {
    message = "Good Morning";
  } else if (hour >= 12 && hour <= 18) {
    message = "Good Afternoon";
  } else {
    message = "Good Evening";
  }

  const slides = [
    {
      url: "https://i.pinimg.com/originals/e1/16/2c/e1162c20fa45aa1f7a1579a90322a7f2.jpg",
      title: "beach",
    },
    {
      url: "https://i.pinimg.com/originals/e1/16/2c/e1162c20fa45aa1f7a1579a90322a7f2.jpg",
      title: "boat",
    },
    {
      url: "https://i.pinimg.com/236x/62/d4/02/62d402306a953e03ad5ad50bf387adcf--may-birth-flowers-may-flowers.jpg",
      title: "forest",
    },
    {
      url: "https://i.pinimg.com/236x/62/d4/02/62d402306a953e03ad5ad50bf387adcf--may-birth-flowers-may-flowers.jpg",
      title: "city",
    },
    {
      url: "https://i.pinimg.com/236x/62/d4/02/62d402306a953e03ad5ad50bf387adcf--may-birth-flowers-may-flowers.jpg",
      title: "italy",
    },
  ];

  const containerStyles = {
    width: "500px",
    height: "280px",
    margin: "0 auto",
  };

  return (
    <div>
    <ImageSlider slides={slides} />
      {/* <div style={containerStyles}>
        <ImageSlider slides={slides} />
      </div> */}

      <div className={styles.wrapper}>
        <div className={styles.scroll}>
          <h1 className={styles.title}>{message}</h1>
        </div>

        <div className={`${styles.fixed} ${styles.one}`}></div>

        <div className={styles.scroll}>
          <h3>
            Take a closer look at our products and see what fits you best!
          </h3>
        </div>

        <div className={`${styles.fixed} ${styles.two}`}></div>

        <div className={styles.scroll}>
          <h3>
            Jewelry is a very personal thing that tell a story about the person
            who is wearing it.
          </h3>
        </div>

        <div className={`${styles.fixed} ${styles.three}`}></div>
      </div>
    </div>
  );
};

export default Welcome;
